import React from "react";
import { Formik, Form, Field } from "formik";
import api from "../../services/api";
import styles from "./styles.css";

function BookRegistration() {
  return (
    <div>
      <h1>Cadastro de Livros</h1>
      <Formik
        initialValues={{
          title: "",
          author: "",
          publisher: "",
          edition: "",
          genre: "",
        }}
        onSubmit={async (values, { setSubmitting }) => {
          const response = await api.post("/book/insert", values);
          console.log(response.data);
          setSubmitting(false);
        }}
      >
        {({ isSubmitting }) => (
          <Form className={styles.form}>
            <Field type="text" name="title" placeholder="Título" />
            <Field type="text" name="author" placeholder="Autor" />
            <Field type="text" name="publisher" placeholder="Editora" />
            <Field type="text" name="edition" placeholder="Edição" />
            <Field type="text" name="genre" placeholder="Gênero" />
            <button type="submit" disabled={isSubmitting}>
              Cadastrar
            </button>
          </Form>
        )}
      </Formik>
    </div>
  );
}

export default BookRegistration;
