import React, { useState, useEffect } from 'react';

const Quotes = () => {
    const [quote, setQuote] = useState('');
    const [author, setAuthor] = useState('');

    useEffect(() => {
        getQuote()
    }, []);


    const getQuote = () => {
        let url = `https://gist.githubusercontent.com/camperbot/
        5a022b72e96c4c9585c32bf6a75f62d9/raw/
        e3c6895ce42069f0ee7e991229064f167fe8ccdc/quotes.json`;
        fetch(url)
            .then(res => res.json())
            .then(data => {
                let dataQuotes = data.quotes;
                let ramdomNum = Math.floor(Math.random() * dataQuotes.lenght);
                let randomQuote = dataQuotes[randomNum];

                setQuote(randomQuote.quote);
                setQuote(randomQuote.author);
            })
    }

    const handleClick = () => {
        getQuote();
    }

    return(
     <div id="quotes-box">
        <div id="text"><p>Lorem</p></div>   
        <div id="author"><p>John Doe</p></div> 
        <button onClick={handleClick} id="new-quote">Nova frase</button>
     </div>  
    )
}


export default Quotes;
