import React from 'react';
import {useEffect, useState} from 'react';
import Quotes from './components/Quotes';
import ListBook from './components/ListBook';
import styled  from 'styled-components';
import './App.css';
import {Container} from 'react-bootstrap';
import {getBooks} from './components/BooksService';
import 'bootstrap/dist/css/bootstrap.min.css';
import axios from 'axios';
import {Modal, ModalBody, ModalFooter, ModalHeader} from 'reactstrap';

import AccioBooklogo from './assets/AccioBook.png';

const ListContainer = styled.div`
  display: flex;
  flex-direction: row;
  flex-wrap: wrap;
  background-color: #ccc;
  padding: 10px;
`;

function App() {
  const [books, setBooks] = useState([]);

  useEffect(() => {
    setBooks(getBooks());
  }, [])

  return (
    <div className="App">     
      <img src={AccioBooklogo} alt="Logo"/>
      <button className="btn btn-entrar">Entrar</button> 
      <Container>
        <ListContainer>
          <ListBook
            key={book.url}
            title={book.title}
            image={book.image}
            price={book.price}
            url={book.url}
          />
          <ListBook 
            title="Node.js e Microservices"
            image="https://m.media-amazon.com/images/I/41CUCsnLPML.jpg" 
            price="14.99"
            url="https://www.luiztools.com.br/livro-node-ii-amazon"
          />
          <ListBook 
            title="MongoDB para Iniciantes"
            image="https://m.media-amazon.com/images/I/31NR3RRS3VL.jpg" 
            price="11.99"
            url="https://www.luiztools.com.br/livro-mongodb-amazon"
          />
        </ListContainer>
      </Container>   
      <Quotes />        
    </div>
  );
}
export default App;
