import React, { useEffect, useState } from "react";
import styled from "styled-components";
import { Container } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";
import "./App.css";

import AccioBooklogo from ".//AccioBook.png";

const ListContainer = styled.div`
  display: flex;
  flex-direction: row;
  background-color: #ccc;
  padding: 10px;
`;

function App() {
  const [books, setBooks] = useState([]);

  useEffect(() => {
    setBooks(getBooks());
  }, []);

  return (
    <div className="App">
      <img src={AccioBooklogo} alt="Logo" />
      <Container>
        <ListContainer>
          {books.map((book) => {
            return (
              <ListBook
                key={book.url}
                title={book.title}
                image={book.image}
                price={book.price}
                url={book.url}
              />
            );
          })}
        </ListContainer>
      </Container>
      <Quotes />
    </div>
  );
}

export default App;
