import React from "react";
import { useEffect, useState } from "react";
import Quotes from "./components/Quotes";
import ListBook from "./components/ListBook";
import styled from "styled-components";
import "./App.css";
import { Container } from "react-bootstrap";
import { getBooks } from "./components/BooksService";
import "bootstrap/dist/css/bootstrap.min.css";
import SearchBar from "./components/SearchBar";
import SearchInput from "./components/SearchInput";

import AccioBooklogo from "./assets/AccioBook.png";

const ListContainer = styled.div`
  display: flex;
  flex-direction: row;
  background-color: #ccc;
  padding: 10px;
`;

function App() {
  const [text, setText] = useState("");

  useEffect(() => {
    if (text) {
      fetch("https://kitsu.io/api/edge/anime?filter[text]=${text}")
        .then((response) => response.json())
        .then((response) => {
          console.log(response);
        });
    }
  }, [text]);

  return (
    <div className="App">
      <img src={AccioBooklogo} alt="Logo" />
      <SearchInput value={text} onChange={(str) => setText(str)} />
    </div>
  );
}

export default App;
