import React from "react";
import { Formik, Form, Field } from "formik";
import api from "../../services/api";
import styles from "./styles.css";

const BookRegistration = ({ handleSubmit, initialValues }) => {
  <Formik initialValues={{ initialValues }} onSubmit={handleSubmit}>
    {({ handleBlur, handleChange, handleSubmit, values }) => (
      <Form className={Form} onSubmit={handleSubmit}>
        <h1 className={Form - Titulo}>Cadastro de Livros</h1>

        <div className="Form-Group">
          <Field type="text" name="title" placeholder="Título" />
        </div>

        <div className="Form-Group">
          <Field type="text" name="author" placeholder="Autor" />
        </div>

        <div className="Form-Group">
          <Field type="text" name="publisher" placeholder="Editora" />
        </div>

        <div className="Form-Group">
          <Field type="text" name="edition" placeholder="Edição" />
        </div>

        <div className="Form-Group">
          <Field type="text" name="genre" placeholder="Gênero" />
        </div>

        <button className="Form-Btn" type="submit" disabled={isSubmitting}>
          Cadastrar
        </button>
      </Form>
    )}
  </Formik>;
};

export default BookRegistration;
